mapper.insert:
INSERT INTO `user` (
    username
    , email
    , password
    , role
    , created_at
) VALUES (
    #{username}
    , #{email}
    , #{password}
    , #{role}
    , NOW()
)

mapper.update:
UPDATE
    `user`
SET
    updated_at = NOW()

        , email = #{email}


        , password = #{password}

WHERE
    username = #{username}
